import React, {useState} from 'react';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import Movie from './components/Movie';
import Favorites from './components/Favorites';
import ComingSoon from './components/ComingSoon';
import Discover from './components/Discover';
import Nav from './components/Nav';
import './App.css';

const MovieApp = _ => {
  const [movies, setMovies] = useState([]);
  async function fetchMovies(query) {
    const url = await fetch('https://api.themoviedb.org/3/search/movie?api_key=2b0d7ae1b2d446dcbb6954fabb182f1e&query=' + query);
    const data = await url.json();
    setMovies(data.results)
  }

  const handleSearchChange = e => {
    const query = e.target.value;
    fetchMovies(query);
  }

  return (
    <Router>
      <div className="movie-container">
        <Nav />
        <input type="text" onChange={handleSearchChange} className="searchbox" placeholder="Which movie are you looking for?" />
        <Route path="/discover" exact component={Discover} />
        <Route path="/coming-soon" exact component={ComingSoon}/>
        <Route path="/favorites" exact component={Favorites} />
        {movies && movies.map((movie, index) => {
          return <Movie key={index} movie={movie}/>
        })}
      </div>
    </Router>
  )
}

export default MovieApp;
