import React from 'react';

const Discover = ({fetchMovies}) => {

  const handleSearchChange = e => {
    const query = e.target.value;
    fetchMovies('search', query);
  }

  return (
    <div className="title-container">
      <h2>Browse/Discover</h2>
      <input type="text" onChange={handleSearchChange} className="searchbox" placeholder="Which movie are you looking for?" />

    </div>
  )
}

export default Discover;
