import React, {useEffect} from 'react';

const Discover = ({fetchMovies}) => {
  useEffect( () => {
    let query= '&primary_release_date.gte=2014-09-15&primary_release_date.lte=2019-08-22';
    fetchMovies('discover',query);
  },[]);
  const handleSearchChange = e => {
    let timeout = null;
     // this is the search text
     let query = e.target.value;
    if(timeout) clearTimeout(timeout);
    timeout = setTimeout(() => {
      fetchMovies('search', query);
      //search function
    }, 1600);

    // let timeout = 0;
    // if(timeout) clearTimeout(timeout);
    // timeout = setTimeout(() => {
    //   let query = e.target.value;
    //   fetchMovies('search', query);
    // }, 1800)
//s    fetchMovies('search', query);
  }

  return (
    <div className="title-container">
      <h2>Browse</h2>
      <input type="text" onChange={(e) => handleSearchChange(e)} className="searchbox" placeholder="Which movie are you looking for?" />

    </div>
  )
}

export default Discover;
