import React, {useState, useEffect} from 'react';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import Movie from './components/Movie';
import Popular from './components/Popular';
import ComingSoon from './components/ComingSoon';
import Discover from './components/Discover';
import Nav from './components/Nav';
import './App.css';

const MovieApp = _ => {
  const [movies, setMovies] = useState([]);
  async function fetchMovies(typeOfQuery,query) {
    const url = await fetch('https://api.themoviedb.org/3/' + typeOfQuery + '/movie?api_key=2b0d7ae1b2d446dcbb6954fabb182f1e&query=' + query);
    const data = await url.json();
    setMovies(data.results)
  }

  const handleSearchChange = e => {
    const query = e.target.value;
    fetchMovies('search', query);
  }

  useEffect( () => {
    let query= '&primary_release_date.gte=2014-09-15&primary_release_date.lte=2019-08-22';
    fetchMovies('discover',query);
  }, []);

  return (
    <Router>
      <div className="movie-container">
        <Nav />
        <input type="text" onChange={handleSearchChange} className="searchbox" placeholder="Which movie are you looking for?" />
        <Route path="/discover" exact component={Discover} />
        <Route path="/coming-soon" exact component={ComingSoon}/>
        <Route
          path="/popular"
          exact
          render={(props)=> <Popular fetchMovies={fetchMovies}/>}
        />
        {movies && movies.map((movie, index) => {
          return <Movie key={index} movie={movie}/>
        })}
      </div>
    </Router>
  )
}

export default MovieApp;
