import React from 'react';

const ComingSoon = ({fetchMovies}) => {
  // let query= '&primary_release_date.gte=2014-09-15&primary_release_date.lte=2019-08-22';
  // fetchMovies('discover',query);

  const handleSearchChange = e => {
    debugger;
    const query = e.target.value;
    fetchMovies('search', query);
  }

  return (
    <div className="title-container">
      <h2>Browse/Discover</h2>
      <input type="text" onChange={handleSearchChange} className="searchbox" placeholder="Which movie are you looking for?" />

    </div>
  )
}

export default ComingSoon;
