import React, {useEffect} from 'react';

const Discover = ({fetchMovies}) => {
  useEffect( () => {
    let query= '&primary_release_date.gte=2014-09-15&primary_release_date.lte=2019-08-22';
    fetchMovies('discover',query);
  },[]);
  const handleSearchChange = e => {
    let timeout = 0;
    if(timeout) clearTimeout(timeout);
    let query = e.target.value;
    timeout = setTimeout(() => {
      fetchMovies('search', query);
    }, 2000)
//s    fetchMovies('search', query);
  }

  return (
    <div className="title-container">
      <h2>Browse</h2>
      <input type="text" onChange={handleSearchChange} className="searchbox" placeholder="Which movie are you looking for?" />

    </div>
  )
}

export default Discover;
