import React from 'react';

const Movie = ({movie}) => {
  console.log(movie);
  const {popularity, poster_path, overview, title, vote_average, vote_count, release_date} = movie;
  const image_url = 'https://image.tmdb.org/t/p/w185' + poster_path;
  return (
    <div className="movie-row">
      <div className="movie-img-container">
        <img src={image_url} alt={title} />
      </div>
      <div className="movie-synopsis-container">
        <h2>{title}</h2>
        {overview}
      </div>
    </div>
  )
}

export default Movie;
