import React from 'react';

const Movie = ({movie}) => {
  const {popularity, poster_path, overview, title, vote_average, vote_count, id, release_date} = movie;
  const image_url = 'https://image.tmdb.org/t/p/w185' + poster_path;
  console.log(typeof release_date);
  const releaseDateArr = release_date.split('-');
  let newReleaseDateArr = [];
  let i=0;
  while(i < releaseDateArr.length) {
    if(i == releaseDateArr - 1) {
      newReleaseDateArr.push(releaseDateArr[0]);
      break;
    }
    newReleaseDateArr.push(releaseDateArr[i+1]);
    i++;
  }

  let newReleaseDateString = newReleaseDateArr.join('-');

  const handleViewClick = _ => {
    const url = 'https://www.themoviedb.org/movie/' + id;
    window.location.href = url;
  }

  return (
    <div className="movie-row">
      <div className="movie-img-container">
        <img src={image_url} alt={title} />
      </div>
      <div className="movie-info-container">
        <h2>{title}</h2>
        <div className="synopsis-overview">{overview}</div>
        <div className="movie-data-container">
          <span className="released-date pill">Released:</span> <span className="data">{newReleaseDateString}</span>
          <span className="vote-average pill">Average rating:</span> <span className="data">{vote_average}</span>
          <button className="view-btn btn" onClick={handleViewClick}>View</button>
        </div>
      </div>
    </div>
  )
}

export default Movie;
